public class Expressions {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 5, y = 10 ;
		int exp1 = x + y * 2 ;
		int exp2 = x - y + 2 ;
		int exp3 = (x+y) * 2 ;
		int exp4 = y%x ;
		System.out.println(exp1);
		System.out.println(exp2);
		System.out.println(exp3);
		System.out.println(exp4);
	

	}

}
